package com.icld.mt.autoupdate.infrastructure.wapapi.net;import java.util.Date;public class Response {	public String Code;	public String Description;	public Date Timestamp;	public String getCode() {		return Code;	}	public void setCode(String code) {		Code = code;	}	public String getDescription() {		return Description;	}	public void setDescription(String description) {		Description = description;	}	public boolean isSuccess() {		return Constants.RESULT_CODE_SUCCESS.equals(Code);	}	public Date getTimestamp() {		return Timestamp;	}	public void setTimestamp(Date timestamp) {		Timestamp = timestamp;	}}